// internalTxKIP17TokenContractBin is the compiled bytecode used for deploying new contracts.
const internalTxKIP17TokenContractBin = ""

// DeployMainContract deploys a new mainContract, binding an instance of DeployMainContract to it.
func DeployMainContract(auth *bind.TransactOpts, backend bind.ContractBackend, addressToken common.Address) (common.Address, *types.Transaction, *bind.BoundContract, error) {
	parsed, err := abi.JSON(strings.NewReader(internalTxContractABI))
	if err != nil {
		return common.Address{}, nil, nil, err
	}
	address, tx, contract, err := bind.DeployContract(auth, parsed, common.FromHex(internalTxContractBin), backend, addressToken, big.NewInt(1), big.NewInt(2))
	if err != nil {
		return common.Address{}, nil, nil, err
	}
	return address, tx, contract, nil
}

// DeployKIP17TokenContract deploys a new mainContract, binding an instance of DeployKIP17TokenContract to it.
func DeployKIP17TokenContract(auth *bind.TransactOpts, backend bind.ContractBackend) (common.Address, *types.Transaction, *bind.BoundContract, error) {
	parsed, err := abi.JSON(strings.NewReader(internalTxKIP17TokenContractABI))
	if err != nil {
		return common.Address{}, nil, nil, err
	}
	address, tx, contract, err := bind.DeployContract(auth, parsed, common.FromHex(internalTxKIP17TokenContractBin), backend)
	if err != nil {
		return common.Address{}, nil, nil, err
	}
	return address, tx, contract, nil
}
